import SwiftUI
import EmojiKit

extension View {
  func customEmojiPicker(
    isPresented: Binding<Bool>,
    title: String = "Emoji",
    onEmojiSelected: @escaping (String) -> Void
  ) -> some View {
    modifier(
      EmojiKitPickerSheetModifier(
        isPresented: isPresented,
        title: title,
        onEmojiSelected: onEmojiSelected
      )
    )
  }
}

private struct EmojiKitPickerSheetModifier: ViewModifier {
  @Binding var isPresented: Bool
  let title: String
  let onEmojiSelected: (String) -> Void

  func body(content: Content) -> some View {
    content.sheet(isPresented: $isPresented) {
      EmojiKitPickerSheet(title: title) { emoji in
        onEmojiSelected(emoji)
        isPresented = false
      }
      #if os(iOS)
      .presentationDragIndicator(.visible)
      .presentationDetents([.medium, .large])
      #endif
    }
  }
}

private struct EmojiKitPickerSheet: View {
  let title: String
  let onEmojiSelected: (String) -> Void

  @Environment(\.dismiss) private var dismiss

  @State private var query = ""
  @State private var selection = Emoji.GridSelection()

  var body: some View {
    NavigationStack {
      EmojiGridScrollView(
        axis: .vertical,
        categories: [.frequent] + .standard,
        query: query,
        selection: $selection,
        registerSelectionFor: [.frequent],
        action: { emoji in
          onEmojiSelected(emoji.char)
          dismiss()
        },
        sectionTitle: { $0.view },
        gridItem: { $0.view }
      )
      .navigationTitle(title)
      .navigationBarTitleDisplayMode(.inline)
      .searchable(text: $query, placement: .navigationBarDrawer(displayMode: .always))
      .toolbar {
        ToolbarItem(placement: .cancellationAction) {
          Button("Cancel") { dismiss() }
        }
      }
    }
    .emojiGridStyle(.standard)
  }
}

